import { GrammarlyEditorPluginCallbacks, ClientId, EditorConfig } from '@grammarly/editor-sdk';
export { ClientId, DocumentStats, Editor, EditorConfig, EditorSDK, SessionStats, SessionSuggestionBreakdown, SuggestionCategories, Time, init } from '@grammarly/editor-sdk';
import { FunctionComponent, HTMLAttributes, ReactNode } from 'react';

/**
 * An optional context provider component for {@link GrammarlyEditorPlugin}.
 *
 * @remarks
 * See {@link GrammarlyProps} for supported props.
 *
 * @public
 */
declare const Grammarly: FunctionComponent<GrammarlyProps>;

/**
 * Show the Grammarly button in a custom position.
 *
 * The Grammarly button can only display once per page. Its location is set and displayed according to the earliest inclusion of `<GrammarlyButton>`.
 *
 * @remarks
 * See {@link GrammarlyButtonProps} for supported props.
 *
 * @public
 * @since v1.5.0
 */
declare const GrammarlyButton: FunctionComponent<GrammarlyButtonProps>;

/**
 * Props for `<GrammarlyButton>`.
 *
 * @public
 * @since v1.5.0
 */
declare interface GrammarlyButtonProps extends HTMLAttributes<HTMLElement> {
    /**
     * Set the position of the tone detector relative to the Grammarly button.
     *
     * @defaultValue "left"
     */
    tonePosition?: "left" | "right";
    /**
     * Set the position of the Grammarly button menu relative to the Grammarly button.
     *
     * @defaultValue "left"
     * @since v2.1.0
     */
    menuPosition?: "left" | "right";
}

/**
 * Component that enhances textareas, inputs or contenteditables with the Grammarly experience.
 *
 * @remarks
 * See {@link GrammarlyEditorPluginProps} for supported props.
 *
 * @public
 */
declare const GrammarlyEditorPlugin: FunctionComponent<GrammarlyEditorPluginProps>;

/**
 * Props for {@link GrammarlyEditorPlugin|`<GrammarlyEditorPlugin>`}.
 *
 * @remarks
 * Permits HTML attributes like `className`, `style`, etc.
 *
 * @public
 */
declare interface GrammarlyEditorPluginProps extends GrammarlyEditorPluginCallbacks, Omit<HTMLAttributes<HTMLElement>, "children"> {
    /** The client ID used to authenticate with Grammarly. */
    clientId?: ClientId;
    /** Optional configuration to customize the Grammarly integration. */
    config?: Partial<EditorConfig>;
    /** Render prop that allows setting underlying editor for advanced uses. */
    children: ReactNode | ((props: {
        setEditor: (editor: HTMLElement | null) => void;
        setViewport: (viewport: HTMLElement | null) => void;
    }) => ReactNode);
}

/**
 * Props for {@link Grammarly|`<Grammarly>`}.
 *
 * @public
 */
declare interface GrammarlyProps {
    /** The client ID used to authenticate with Grammarly. */
    clientId?: ClientId;
    /** Optional configuration to customize the Grammarly integration. */
    config?: Partial<EditorConfig>;
}

export { Grammarly, GrammarlyButton, GrammarlyButtonProps, GrammarlyEditorPlugin, GrammarlyEditorPluginProps, GrammarlyProps };
